<?phpnamespace NlpToolkit;class Text{	protected $tagger = null;	protected $tokenizer = null;	protected $segmenter = null;	protected $text = null;	public function __construct($text, $segmenter=null, $tokenizer=null, $tagger=null){		if(!$segmenter){			$segmenter = new RegexSentenceTokenizer();		}				if(!$tokenizer){			$tokenizer = new WordTokenizer();		}				if(!$tagger){			$tagger = new BrillTagger(dirname(__FILE__) . "/lexicon.txt");		}				$this->tokenizer = $tokenizer;		$this->segmenter = $segmenter;		$this->tagger = $tagger;		$this->text = $text;				$this->init();	}		protected function init(){		$sentences = $this->segmenter->tokenize($this->text);		$this->sentences = array();		for($i = 0; $i < count($sentences); $i++){			$tokens = $this->tokenizer->tokenize($sentences[$i]);			$taggedTokens = $this->tagger->tag($tokens);						$toks = array();			for($j = 0; $j < count($taggedTokens); $j++){				array_push($toks, new Token($this, 					$taggedTokens[$j]['word'], $taggedTokens[$j]['tag'], $i, $j));			}						array_push($this->sentences, $toks);		}	}		public function getSentences(){		return $this->sentences;	}		public function getTokens(){		$tokens = array();		foreach($this->sentences as $s){			$tokens = array_merge($tokens, $s);		}		return $tokens;	}	}?>