<?phpnamespace NlpToolkit;class Token{	protected $tag = null;	protected $word = null;	protected $sentenceOffset = null;	protected $wordOffset = null;	public function __construct($text, $word, $tag, $sentenceOffset, $wordOffset){		$this->word = $word;		$this->tag = $tag;		$this->sentenceOffset = $sentenceOffset;		$this->wordOffset = $wordOffset;	}		public function tag(){		return $this->tag;	}		public function word(){		return $this->word;	}		public function sentenceOffset(){		return $this->sentenceOffset;	}		public function wordOffset(){		return $this->wordOffset;	}		public function lemma(){		// TODO: Implement this using the lemmatizer in Text		return null;	}		public function __toString(){		return $this->word() . "/" . $this->tag();	}		}?>